import Header from "./Header";
import Main from "./Main";
import Footer from "./Footer";
import ImagePopup from "./ImagePopup";
import {useEffect, useState} from "react";
import api from "../utils/api";
import {CurrentUserContext} from "../contexts/CurrentUserContext";
import EditProfilePopup from "./EditProfilePopup";
import EditAvatarPopup from "./EditAvatarPopup";
import AddPlacePopup from "./AddPlacePopup";
import ConfirmDeletePopup from "./ConfirmDeletePopup";
import {Route, Switch, useHistory} from "react-router-dom";
import Login from "./Login";
import Register from "./Register";
import ProtectedRoute from "./ProtectedRoute";
import InfoTooltip from "./InfoTooltip";
import * as MestoAuth from "../MestoAuth";

function App() {

    const [currentUser, setCurrentUser] = useState({
        name: '',
        about: '',
        avatar: '',
        email: '',
    })

    const [infoTooltipData, setInfoToolTipData] = useState({
        title: '',
        image: '',
        isOpen: false,
    })

    const [selectedCard, setSelectedCard] = useState(null);

    const [isEditProfilePopupOpen, setEditProfilePopup] = useState(false);

    const [isAddPlacePopupOpen, setAddPlacePopup] = useState(false);

    const [isEditAvatarPopupOpen, setEditAvatarPopup] = useState(false);

    const [isDeleteConfirmPopupOpen, setDeleteConfirmPopup] = useState(false);

    const [isImagePopupOpen, setImagePopupOpen] = useState(false);

    const [cards, setCards] = useState([]);

    const [loggedIn, setLoggedIn] = useState(false);

    const history = useHistory();

    useEffect(() => {
        api.getProfile()
            .then(
            (userData) => {
                setCurrentUser({
                    ...currentUser,
                    name: userData.name,
                    about: userData.about,
                    avatar: userData.avatar,
                    _id: userData._id
                })
            })
            .catch(
                (error) => {
                    console.log(
                        `Произошла ошибка при получении данных профиля пользователя - ${error}`
                );
        })
    }, [loggedIn]);

    useEffect(() => {
        api.getCards().then(
            (cardsData) => {
                setCards(cardsData);
            })
            .catch(
            (error) => {
                console.log(
                    `Произошла ошибка получении карточек с сервера - ${error}`
                );
            }
        )}, [loggedIn]);

    function handleEditAvatarClick() {
        setEditAvatarPopup(true);
    }

    function handleEditProfileClick() {
        setEditProfilePopup(true);
    }

    function  handleAddPlaceClick() {
        setAddPlacePopup(true);
    }

    function closeAllPopups() {
        setEditAvatarPopup(false);
        setEditProfilePopup(false);
        setAddPlacePopup(false);
        setDeleteConfirmPopup(false);
        setImagePopupOpen(false);
        setInfoToolTipData({
            ...infoTooltipData,
            isOpen: false
        });
        setSelectedCard(null);
    }

    function handleCardClick(cardData) {
        setSelectedCard(cardData);
        setImagePopupOpen(true);
    }

    function handleDeleteConfirmClick(cardData) {
        setDeleteConfirmPopup(true);
        setSelectedCard(cardData);
    }

    function handleUpdateProfile(data) {
        api.updateProfile(data)
            .then((res) => {

                setCurrentUser({
                    ...currentUser,
                    name: res.name,
                    about: res.about,
                })

                closeAllPopups();
            })
            .catch(
            (error) => {
                console.log(
                    `Произошла ошибка при обновлении профиля пользователя - ${error}`
                );
            })
    }

    function handleUpdateAvatar(newUrl) {
        api.updateAvatar(newUrl.avatar)
            .then((res) => {
                setCurrentUser({
                    ...currentUser,
                    avatar: res.avatar,
                })

                closeAllPopups();
            })
            .catch(
            (error) => {
                console.log(
                    `Произошла ошибка при обновлении аватара пользователя - ${error}`
                );
            }
        )
    }

    function handleAddPlaceSubmit(newData) {
        api.addCard(newData)
            .then((newCard) => {
                setCards([newCard, ...cards])

                closeAllPopups();
            })
            .catch(
            (error) => {
                console.log(
                    `Произошла ошибка при создании новой карточки - ${error}`
                );
            }
        )
    }

    function handleCardLike(card) {
        const { likes, _id } = card;
        // проверяем лайк
        const isLiked = likes.some(like => like._id === currentUser._id);
        // отправляем запрос в АПИ и получаем обновленные данные карточки
        api.changeLikeCardStatus(_id, !isLiked)
            .then((newCard) => {
                setCards(
                    (state) => state.map(
                        c => c._id === card._id ? newCard : c
                    )
                )
            })
            .catch(
            (error) => {
                console.log(
                    `Произошла ошибка при лайке/дизлайке карточки - ${error}`
                );
            }
        );
    }

    function handleCardRemove(card) {
        api.deleteCard(card._id)
            .then(() => {

                setCards(
                    (state) => state.filter((
                            c => c._id !== card._id
                        )
                    ))
            })
            .catch(
            (error) => {
                console.log(
                    `Произошла ошибка при удалении карточки - ${error}`
                );
            }
        )
    }


    useEffect(() => {

        const jwt = localStorage.getItem('jwt');

        if (jwt) {
            auth(jwt).then((res) => {});
        }
    }, [loggedIn]);

    useEffect(() => {

        if (loggedIn) {
            history.push('/');
        }
    }, [loggedIn]);


    const auth = async (jwt) => {
        return await MestoAuth.getContent(jwt)
            .then(
                (res) => {

                    const { data } = res;

                    setCurrentUser({
                        ...currentUser,
                        email: data.email,
                    })

                    setLoggedIn(true);
                });
    }

    function onSignOut() {}

    function onLogin({email, password}) {
        return MestoAuth.authorize(email, password)
            .then((res) => {

                const { token } = res;

                if (token) {
                    localStorage.setItem('jwt', token);
                    setLoggedIn(true);
                }
        });
    }

    function onRegister({ email, password }) {
        return MestoAuth.register(email, password)
            .then((res) => {
                if (res.ok) {

                    const { data } = res;

                    setCurrentUser({
                        ...currentUser,
                        email: data.email,
                    });

                    setInfoToolTipData({
                        ...infoTooltipData,
                        title: "Вы успешно зарегистрировались!",
                        image: "../images/tooltip/success.svg",
                        isOpen: true,
                    })

                    setTimeout(() => {
                        closeAllPopups();
                        history.push("/sign-in");
                    }, 3000);
                } else {

                    setInfoToolTipData({
                        ...infoTooltipData,
                        title: "Что-то пошло не так! Попробуйте ещё раз.",
                        image: "../images/tooltip/failure.svg",
                        isOpen: true,
                    })

                    setTimeout(() => {
                        closeAllPopups();
                        history.push("/sign-in");
                    }, 3000);
                }
            })
    }

    return (
        <CurrentUserContext.Provider value={currentUser}>
            <div className="App">
                <Header email={currentUser.email} onSignOut={onSignOut} />
                <Switch>
                    <ProtectedRoute
                        exact
                        path="/"
                        component={Main}
                        loggedIn={loggedIn}
                        onEditAvatar={handleEditAvatarClick}
                        onEditProfile={handleEditProfileClick}
                        onAddPlace={handleAddPlaceClick}
                        onCardClick={handleCardClick}
                        onRemoveBtnClick={handleDeleteConfirmClick}
                        cards={cards}
                        onCardLike={handleCardLike}
                    />
                    <Route path="/sign-in">
                        <Login onLogin={onLogin}>
                            <InfoTooltip
                                isOpen={true}
                                onClose={closeAllPopups}
                            />
                        </Login>
                    </Route>
                    <Route path="/sign-up">
                        <Register onRegister={onRegister} closeAllPopups={closeAllPopups} />
                        <InfoTooltip
                            title={infoTooltipData.title}
                            image={infoTooltipData.image}
                            isOpen={infoTooltipData.isOpen}
                            onClose={closeAllPopups}
                        />
                    </Route>
                </Switch>
                    <Footer />
                    <EditProfilePopup
                        isOpen={isEditProfilePopupOpen}
                        onClose={closeAllPopups}
                        onUpdateUserProfile={handleUpdateProfile}
                    />
                    <AddPlacePopup
                        isOpen={isAddPlacePopupOpen}
                        onClose={closeAllPopups}
                        onAddPlace={handleAddPlaceSubmit}
                    />
                    <EditAvatarPopup
                        isOpen={isEditAvatarPopupOpen}
                        onClose={closeAllPopups}
                        onUpdateAvatar={handleUpdateAvatar}
                    />
                    <ConfirmDeletePopup
                        formClassName="popup popup_id_delete-confirm"
                        name="delete-confirm"
                        wrapperClass="popup__wrapper popup__wrapper_type_confirm"
                        title="Вы уверены?"
                        submitButtonTitle="Да"
                        onRemoveCard={handleCardRemove}
                        isOpen={isDeleteConfirmPopupOpen}
                        onClose={closeAllPopups}
                        card={selectedCard}
                    />
                    <ImagePopup
                        card={selectedCard}
                        isOpen={isImagePopupOpen}
                        onClose={closeAllPopups}
                    />
            </div>
        </CurrentUserContext.Provider>
    );
}

export default App;
