import React from 'react';
import PopupWithForm from "./PopupWithForm";
import {useState} from 'react';

const Login = ({ onLogin }) => {

    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [message, setMessage] = useState('');

    function resetForm() {
        setEmail('');
        setPassword('');
    }

    function handleChangeEmail(evt) {
        setEmail(evt.target.value);
    }

    function handleChangePassword(evt) {
        setPassword(evt.target.value);
    }

    function handleSubmit(evt) {
        evt.preventDefault();

        onLogin({email, password})
            .then(() => {
            history.push('/');
        })
            .then(() => resetForm())
            .catch((err) => {
                setMessage(err);
            })
    }

    return (
        <PopupWithForm
            formClassName="login"
            name="login"
            wrapperClass="login__wrapper"
            title="Вход"
            submitButtonTitle="Войти"
            onSubmit={handleSubmit}
        >
            <label className="login__label">
                <input
                    type="email"
                    className="login__input register_type_email"
                    id="login-email"
                    name="email"
                    aria-label="Поле ввода электронной почты пользователя"
                    placeholder="Email"
                    onChange={handleChangeEmail}
                    value={email}
                    pattern="[a-z0-9._%+-]+@[a-z0-9.-]+\.[a-z]{2,4}$"
                    autoComplete="off"
                    required
                />
                <span className="login__input-error login__input-error_type_email"></span>
            </label>
            <label className="login__label">
                <input
                    type="password"
                    className="login__input login__input_type_password"
                    id="login-password"
                    name="password"
                    aria-label="Поле ввода пароля"
                    placeholder="Пароль"
                    onChange={handleChangePassword}
                    value={password}
                    minLength="6"
                    maxLength="20"
                    autoComplete="off"
                    required
                />
                <span className="login__input-error login__input-error_type_password"></span>
            </label>
        </PopupWithForm>
    );
};

export default Login;
